import React, {useReducer, useEffect} from 'react'
import axios from 'axios'


const initialState = {
    loading : true,
    error : '',
    post : {}
}
const reducer = (state, action) => {
    switch(action.type){
        case 'success':
            return {
                loading : true,
                post : action.payload
            }
            break
        case 'error':
            return {
                loading : false,
                error : 'Something went wrong'
            }
            break
        default:
            return {
                loading : false
            }

    }
}

function UseReducerDataFetching() {

    const [loading,dispatch] = useReducer(reducer, initialState)

    useEffect(() => {
        axios.get(`http://jsonplaceholder.typicode.com/posts/1`)
        .then(res => {
            console.log(res)
            dispatch('success')
        })
        .catch(error => {
            dispatch('error')
            console.log(error)
        })
    },[])

    return (
        <div>
            {loading ? 'Loading' : post.title}
            {error ? error : null}
        </div>
    )
}

export default UseReducerDataFetching
